import React, { useEffect, useState } from 'react';
import { fetchNews } from '../services/newsService';

const News = () => {
  const [news, setNews] = useState([]);

  useEffect(() => {
    const getNews = async () => {
      const data = await fetchNews();
      setNews(data);
    };
    getNews();
  }, []);

  return (
    <div>
      <h2>News</h2>
      {news.map((article) => (
        <div key={article._id}>
          <h3>{article.title}</h3>
          <p>{article.description}</p>
          <a href={article.url}>Read more</a>
        </div>
      ))}
    </div>
  );
};

export default News;
